onClick:
{{
  (() => {
    // 1️⃣ Get selected labels (HP / HEAD / DISCHARGE)
    const labels = MultiSelect_Params.selectedOptionLabels || [];

    // 2️⃣ Enforce exactly 2 parameters
    if (labels.length !== 2) {
      showAlert("Please select exactly 2 parameters.", "warning");
      return;
    }

    // 3️⃣ Decode which ones were selected (order-independent)
    const hasHP = labels.includes("HP");
    const hasHead = labels.includes("HEAD");
    const hasDischarge = labels.includes("DISCHARGE");

    let queryToRun;

    if (hasHP && hasDischarge) {
      queryToRun = hp_discharge;      // HP + DISCHARGE query
    } else if (hasHead && hasDischarge) {
      queryToRun = head_discharge;    // HEAD + DISCHARGE query
    } else if (hasHP && hasHead) {
      queryToRun = hp_head;           // HP + HEAD query
    } else {
      showAlert("This combination of parameters is not supported.", "warning");
      return;
    }

    // 4️⃣ Run the selected query
    return queryToRun
      .run()
      .then((data) => {
        const row = data && data[0];

        if (row) {
          Text_Result.setText(
            `Best model: ${row.model_code}\n` +
            `HP: ${row.hp}\n` +
            `Head: ${row.head_m} m\n` +
            `Discharge: ${row.discharge_lpm} LPM`
          );
        } else {
          Text_Result.setText(
            "No matching model found for the given parameters."
          );
        }
      })
      .catch((err) => {
        showAlert("Error while finding model: " + err.message, "error");
      });
  })()
}}
